// Example: 11/11/2022 3:00:00 AM
export const formatDateTime = (date: Date | string, options?: { dateMessage: string, timeMessage: string }) => {
    if (date === null || date === undefined) {
        if (options !== undefined) {
            return {
                date: options.dateMessage,
                time: options.timeMessage
            };
        } else {
            return {
                date: 'Invalid date',
                time: 'Invalid time'
            };
        }
    }
    else if (date === "") {        
        return {
            date: "",
            time: ""
        };
    }
    else {
        const timeString = new Date(date);
        const formattedDate = timeString.toLocaleDateString('en-US');
        const formattedTime = timeString.toLocaleTimeString('en-US');
        return {
            date: formattedDate,
            time: formattedTime,
        };
    }
};
//Example: 3:43 PM
export const formatTimeHHMM = (time: Date | string, options?: { message: string }) => {
    if (time === null || time === undefined) {
        if (options !== undefined) {
            return options.message;
        } else {
            return 'Invalid time';
        }
    } else if (time === "") {
        return "";
    }
    else {
        const timeString = new Date(time);         
        const formattedTime = timeString.toLocaleTimeString('en-US', { hour12: true, hour: "2-digit", minute: "2-digit" });
        return formattedTime;
    }
};

// Example: 11/11/2022
export const formatDateMMDDYYYY = (date: Date | string, options?: { message: string }) => {    
    if (date === null || date === undefined) {
        if (options !== undefined) {
            return options.message;
        } else {
            return 'Invalid date';
        }
    }
    else if (date === "") {
        return "";
    }
    else {
        const timeString = new Date(date);
        const formattedDate = timeString.toLocaleDateString('en-US');

        return formattedDate;
    }    
};